<?phpnamespace Shipin\Controller;class IndexController extends MobileCommonController{    //远程远教首页    public function index()    {        $ids = M('category')->where(array('pid' => 58))->Field('id,name')->select();        $this->assign('ids', $ids);        $datas = M('category')            ->join('my_soft soft on my_category.id=soft.cid')            ->field("my_category.id cat_id,soft.id,soft.title,soft.litpic,soft.downlink,soft.description")//需要显示的字段            ->where(array('my_category.pid' => 58))            ->select();        $this->assign('lists', $datas);        $this->display();    }    public function detail()    {        $id = I('get.id');        $info = M('soft')->where(array('id' => $id))->find();        var_dump($info);        $this->assign('info', $info);        $this->assign('title', C('CFG_WEBNAME'));        $this->display('video-detail');    }}