<?phpnamespace Toupiao\Controller;class IndexController extends MobileCommonController{    //远程远教首页    public function index()    {        $datas = M('product')->where(array('status' => 1))->select();//        $info = M('product')->where(array('status' => 1))->Field('id,title,piao')->order('piao desc')->select();        $this->assign('info', $info);        $this->assign('lists', $datas);        $this->display();    }    public function detail()    {        $id = I('get.id');        $info = M('product')->where(array('id' => $id))->find();        $this->assign('info', $info);        $this->assign('title', C('CFG_WEBNAME'));        $this->display('image-detail');    }    public function result()    {        $info = M('product')->where(array('status' => 1))->Field('id,title,piao')->order('piao desc')->select();        $this->assign('info', $info);        $this->assign('title', C('CFG_WEBNAME'));        $this->display();    }    public function toupiao()    {        $id = I('get.id');        $ip = I('get.id');        $num = M('product_log')->where(array('ip' => $ip))->count();        if($num){        }else{            $yes = M('product')->where(array('id' => $id))->setInc('piao');        }    }}